import { tags } from "typia";

/**
 * Summary of poll for list/search views. Contains minimal poll data for
 * listings: id, post reference, title, multi_choice flag, open/close and
 * creation times. Related to ISummaryList APIs.
 *
 * @autobe Generated by AutoBE - https://github.com/wrtnlabs/autobe
 */
export type IDiscussionBoardPollSummary = {
  /** Unique identifier for the poll. Primary key in discussion_board_polls. */
  id: string & tags.Format<"uuid">;

  /**
   * ID of the post to which this poll is attached. References
   * discussion_board_posts.id. Prisma:
   * discussion_board_polls.discussion_board_post_id
   */
  discussion_board_post_id: string & tags.Format<"uuid">;

  /**
   * Poll title as displayed to users. Must be unique per post. Prisma:
   * discussion_board_polls.title
   */
  title: string;

  /**
   * True if poll allows multiple choices per user. False for single-choice
   * polls. Prisma: discussion_board_polls.multi_choice
   */
  multi_choice: boolean;

  /**
   * Time when poll opened (UTC ISO 8601). Used to enforce voting windows.
   * Prisma: discussion_board_polls.opened_at
   */
  opened_at: string & tags.Format<"date-time">;

  /**
   * Time poll closes (null if poll remains open). Prisma:
   * discussion_board_polls.closed_at
   */
  closed_at?: (string & tags.Format<"date-time">) | null;

  /** Creation timestamp. Prisma: discussion_board_polls.created_at */
  created_at: string & tags.Format<"date-time">;

  /**
   * Soft deletion marker. If set, poll is hidden/deleted. Prisma:
   * discussion_board_polls.deleted_at
   */
  deleted_at?: (string & tags.Format<"date-time">) | null;
};
